ServiceReview_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_29': 'ServiceReview_mobilebutton_29',
        'mobilelabel_63': 'ServiceReview_mobilelabel_63',
        'mobilelabel_ProductName': 'ServiceReview_mobilelabel_ProductName',
        'spacer_61': 'ServiceReview_spacer_61',
        'mobilelabel_15': 'ServiceReview_mobilelabel_15',
        'mobilelist_53': 'ServiceReview_mobilelist_53',
        'mobilelistitem_54': 'ServiceReview_mobilelistitem_54',
        'mobilelistitembutton_55': 'ServiceReview_mobilelistitembutton_55',
        'mobiletextarea_60': 'ServiceReview_mobiletextarea_60'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'ServiceReview';

    /*
     * Nonvisual components
     */
    var datasources = [];

    Reviewrestservice1 = new Appery.DataSource(RESTServiceReview, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("ServiceReview");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [{
            'PATH': ['records'],
            'ID': 'mobilelistitem_54',
            'SET': [{
                'PATH': ['formatted'],
                'ID': 'mobiletextarea_60',
                'ATTR': 'value'
            }]
        }],
        'requestMapping': [{
            'PATH': ['server'],
            'TYPE': 'STRING',
            'ID': '___local_storage___',
            'ATTR': 'Instance_url'
        }, {
            'PATH': ['token'],
            'TYPE': 'STRING',
            'ID': '___local_storage___',
            'ATTR': 'access_token'
        }, {
            'PATH': ['product_code'],
            'TYPE': 'STRING',
            'ID': '___local_storage___',
            'ATTR': 'productcode'
        }]
    });

    datasources.push(Reviewrestservice1);

    /*
     * Events and handlers
     */

    // Before Show
    ServiceReview_beforeshow = function() {
        Appery.CurrentScreen = "ServiceReview";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_FEFD_onLoad = ServiceReview_onLoad = function() {
        screen_FEFD_elementsExtraJS();
        setText('ServiceReview_mobilelabel_ProductName', localStorage.getItem('productname'));
        try {
            Reviewrestservice1.execute({})
        } catch (ex) {
            console.log(ex.name + '  ' + ex.message);
            hideSpinner();
        };

        // TODO fire device events only if necessary (with JS logic)
        ServiceReview_deviceEvents();
        ServiceReview_windowEvents();
        screen_FEFD_elementsEvents();
    }

    // screen window events
    screen_FEFD_windowEvents = ServiceReview_windowEvents = function() {

        $('#ServiceReview').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });

    }

    // device events
    ServiceReview_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_FEFD_elementsExtraJS = ServiceReview_elementsExtraJS = function() {
        // screen (ServiceReview) extra code

        /* mobilelist_53 */

        listView = $("#ServiceReview_mobilelist_53");
        theme = listView.attr("data-theme");
        if (typeof theme !== 'undefined') {
            var themeClass = "ui-btn-up-" + theme;
            listItem = $("#ServiceReview_mobilelist_53 .ui-li-static");
            $.each(listItem, function(index, value) {
                $(this).addClass(themeClass);
            });
        }

        /* mobilelistitem_54 */

    }

    // screen elements handler
    screen_FEFD_elementsEvents = ServiceReview_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#ServiceReview_mobileheader [name="mobilebutton_29"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    Appery.navigateTo('startScreen', {
                        transition: 'slide',
                        reverse: false
                    });

                }
            },
        });

    }

    $("#ServiceReview").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        ServiceReview_beforeshow();
    });

    if (runBeforeShow) {
        ServiceReview_beforeshow();
    } else {
        ServiceReview_onLoad();
    }

}

$("#ServiceReview").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    ServiceReview_js();
});