startScreen_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilelabel_30': 'startScreen_mobilelabel_30',
        'mobilebutton_31': 'startScreen_mobilebutton_31',
        'mobilelabel_76': 'startScreen_mobilelabel_76',
        'mobilesearchbar_74': 'startScreen_mobilesearchbar_74',
        'spacer_41': 'startScreen_spacer_41',
        'mobilelist_63': 'startScreen_mobilelist_63',
        'mobilelistitem_64': 'startScreen_mobilelistitem_64',
        'mobilelistitembutton_65': 'startScreen_mobilelistitembutton_65',
        'mobilelabel_70Business': 'startScreen_mobilelabel_70Business'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'startScreen';

    /*
     * Nonvisual components
     */
    var datasources = [];

    restservice1 = new Appery.DataSource(Salesforce_Account_query_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("startScreen");
        },
        'onSuccess': function(data) {
            try {
                restservice1.execute({})
            } catch (ex) {
                console.log(ex.name + '  ' + ex.message);
                hideSpinner();
            };
        },
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [{
            'PATH': ['$', 'records'],
            'ID': 'mobilelistitem_64',
            'SET': [{
                'PATH': ['Name'],
                'ID': 'mobilelabel_70Business',
                'ATTR': '@'
            }]
        }],
        'requestMapping': [{
            'PATH': ['Authorization'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{salesforce_access_token}'
        }, {
            'PATH': ['q'],
            'TYPE': 'STRING',
            'ID': 'mobilesearchbar_74',
            'ATTR': 'value',
            'TRANSFORMATION': function(value) {
                return "Select Name from Account where Name like '%" + value + "%'";
            }
        }, {
            'PATH': ['appery-proxy-url'],
            'HEADER': true,
            'ATTR': '{salesforce_instance_url}/services/data/{salesforce_api_version}/query'
        }, {
            'PATH': ['appery-key'],
            'HEADER': true,
            'ATTR': '1384818350503'
        }, {
            'PATH': ['appery-rest'],
            'HEADER': true,
            'ATTR': '2780442'
        }]
    });

    datasources.push(restservice1);

    /*
     * Events and handlers
     */

    // Before Show
    startScreen_beforeshow = function() {
        Appery.CurrentScreen = "startScreen";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_3671_onLoad = startScreen_onLoad = function() {
        screen_3671_elementsExtraJS();

        // TODO fire device events only if necessary (with JS logic)
        startScreen_deviceEvents();
        startScreen_windowEvents();
        screen_3671_elementsEvents();
    }

    // screen window events
    screen_3671_windowEvents = startScreen_windowEvents = function() {

        $('#startScreen').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });

    }

    // device events
    startScreen_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_3671_elementsExtraJS = startScreen_elementsExtraJS = function() {
        // screen (startScreen) extra code

        /* mobilelist_63 */

        listView = $("#startScreen_mobilelist_63");
        theme = listView.attr("data-theme");
        if (typeof theme !== 'undefined') {
            var themeClass = "ui-btn-up-" + theme;
            listItem = $("#startScreen_mobilelist_63 .ui-li-static");
            $.each(listItem, function(index, value) {
                $(this).addClass(themeClass);
            });
        }

        /* mobilelistitem_64 */

    }

    // screen elements handler
    screen_3671_elementsEvents = startScreen_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#startScreen_mobilecontainer1 [name="mobilebutton_31"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    salesforceLogin();

                }
            },
        });

        $('#startScreen_mobilecontainer1 [name="mobilesearchbar_74"]').die().live({
            keypress: function() {
                try {
                    restservice1.execute({})
                } catch (ex) {
                    console.log(ex.name + '  ' + ex.message);
                    hideSpinner();
                };
            },
            swipe: function() {
                try {
                    restservice1.execute({})
                } catch (ex) {
                    console.log(ex.name + '  ' + ex.message);
                    hideSpinner();
                };
            },
        });
        $('#startScreen_mobilesearchbar_74-cover-img').live({
            vclick: function() {
                if (!$(this).attr('disabled')) {
                    try {
                        restservice1.execute({})
                    } catch (ex) {
                        console.log(ex.name + '  ' + ex.message);
                        hideSpinner();
                    };
                }
            }
        });

    }

    $("#startScreen").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        startScreen_beforeshow();
    });

    if (runBeforeShow) {
        startScreen_beforeshow();
    } else {
        startScreen_onLoad();
    }

}

$("#startScreen").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    startScreen_js();
});